--  DDL for View V_ACCESOS_CLIENTES
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "ESTUDIANTE"."V_ACCESOS_CLIENTES" ("ACCESO_ID", "NOMBRE_CLIENTE", "FECHA_ENTRADA", "FECHA_SALIDA") AS 
  SELECT 
    a.acceso_id, 
    c.nombre AS nombre_cliente, 
    a.fecha_entrada, 
    a.fecha_salida
FROM 
    Accesos a
JOIN 
    Clientes c ON a.cliente_id = c.cliente_id
WHERE 
    a.fecha_entrada > CURRENT_TIMESTAMP - INTERVAL '7' DAY
;
