  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "ESTUDIANTE"."V_FACTURACION_MENSUAL" ("CLIENTE_ID", "NOMBRE_CLIENTE", "TOTAL_FACTURADO", "MES") AS 
  SELECT 

    c.cliente_id, 

    c.nombre AS nombre_cliente, 

    SUM(f.monto) AS total_facturado, 

    TO_CHAR(f.fecha_factura, 'YYYY-MM') AS mes

FROM 

    Facturacion f

JOIN 

    Clientes c ON f.cliente_id = c.cliente_id

GROUP BY 

    c.cliente_id, c.nombre, TO_CHAR(f.fecha_factura, 'YYYY-MM')
;
