  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "ESTUDIANTE"."V_RUTINAS_CLIENTE" ("RUTINA_ID", "NOMBRE_CLIENTE", "NOMBRE_ENTRENADOR", "DESCRIPCION", "FECHA_ASIGNACION") AS 
  SELECT 
    r.rutina_id, 
    c.nombre AS nombre_cliente, 
    e.nombre_entrenador, 
    r.descripcion, 
    r.fecha_asignacion
FROM 
    Rutinas r
JOIN 
    Clientes c ON r.cliente_id = c.cliente_id
JOIN 
    Entrenadores e ON r.entrenador_id = e.entrenador_id
;
