set define off;

  CREATE OR REPLACE NONEDITIONABLE PROCEDURE "ESTUDIANTE"."AGREGAR_MEMBRESIA_A_CLIENTE" ( 
    p_membresia_id IN NUMBER,          -- FK to the MEMBRESIA table
    p_cliente_id IN NUMBER,            -- FK to the CLIENTES table
    p_fecha_inicio IN DATE,            -- Start date of the membership
    p_fecha_fin IN DATE,               -- End date of the membership
    p_estado IN VARCHAR2               -- Status of the membership
) AS
    v_membresia_exists NUMBER;  -- Variable to check if membership exists
    v_cliente_exists NUMBER;    -- Variable to check if client exists
BEGIN
    -- Validate that the provided Membresia_ID exists in the MEMBRESIA table
    SELECT COUNT(*)
    INTO v_membresia_exists
    FROM MEMBRESIA
    WHERE MEMBRESIA_ID = p_membresia_id;

    IF v_membresia_exists = 0 THEN
        RAISE_APPLICATION_ERROR(-20001, 'El ID de Membresa no existe.');
    END IF;

    -- Validate that the provided Cliente_ID exists in the CLIENTES table
    SELECT COUNT(*)
    INTO v_cliente_exists
    FROM CLIENTES
    WHERE CLIENTE_ID = p_cliente_id;

    IF v_cliente_exists = 0 THEN
        RAISE_APPLICATION_ERROR(-20002, 'El ID de Cliente no existe.');
    END IF;

    -- Insert the membership-client relationship with the specified details
    INSERT INTO MEMBRESIAS (
        MEMBRESIA_ID, 
        CLIENTE_ID, 
        FECHA_INICIO, 
        FECHA_FIN, 
        ESTADO
    ) VALUES (
        p_membresia_id,
        p_cliente_id,
        p_fecha_inicio,
        p_fecha_fin,
        p_estado
    );

    -- Output a success message
    DBMS_OUTPUT.PUT_LINE('Membresa asignada exitosamente al cliente.');

EXCEPTION
    WHEN OTHERS THEN
        -- Catch and handle unexpected errors
        RAISE_APPLICATION_ERROR(-20099, 'Error al agregar la Membresa al Cliente: ' || SQLERRM);
