--  DDL for Procedure ASIGNAR_ENTRENADOR_CLASE
--------------------------------------------------------
set define off;

  CREATE OR REPLACE NONEDITIONABLE PROCEDURE "ESTUDIANTE"."ASIGNAR_ENTRENADOR_CLASE" (
    p_entrenador_id IN NUMBER,
    p_clase_id IN NUMBER,
    p_message OUT VARCHAR2
) AS
    v_count NUMBER;
BEGIN
    -- Check if the provided ENTRIANDOR_ID exists in the ENTRENADORES table
    SELECT COUNT(*) INTO v_count
    FROM ENTRENADORES
    WHERE ENTRENADOR_ID = p_entrenador_id;

    IF v_count = 0 THEN
        p_message := 'Error: Entrenador no existe.';
        RETURN;
    END IF;

    -- Check if the provided CLASE_ID exists in the CLASES table
    SELECT COUNT(*) INTO v_count
    FROM CLASES
    WHERE CLASE_ID = p_clase_id;

    IF v_count = 0 THEN
        p_message := 'Error: Clase no existe.';
        RETURN;
    END IF;

    -- Check if the relationship already exists in the ENTRENADOR_CLASE table
    SELECT COUNT(*) INTO v_count
    FROM ENTRENADOR_CLASE
    WHERE ENTRENADOR_ID = p_entrenador_id AND CLASE_ID = p_clase_id;

    IF v_count > 0 THEN
        p_message := 'Error: El entrenador ya est asignado a esta clase.';
        RETURN;
    END IF;

    -- Insert into the ENTRENADOR_CLASE table to link trainer to class
    INSERT INTO ENTRENADOR_CLASE (ENTRENADOR_ID, CLASE_ID)
    VALUES (p_entrenador_id, p_clase_id);

    p_message := 'Entrenador asignado a la clase correctamente.';
EXCEPTION
    WHEN OTHERS THEN
        -- Handle any unexpected errors
        p_message := 'Error: ' || SQLERRM;
END ASIGNAR_ENTRENADOR_CLASE;

