--  DDL for Procedure CONSULTAFACTURACION
--------------------------------------------------------
set define off;

  CREATE OR REPLACE NONEDITIONABLE PROCEDURE "ESTUDIANTE"."CONSULTAFACTURACION" (
    p_cliente_id IN NUMBER,            -- Client ID (foreign key)
    p_facturacion_info OUT SYS_REFCURSOR   -- Output cursor for the result
) AS
BEGIN
    -- Open the cursor to retrieve invoice details and client name from the related tables
    OPEN p_facturacion_info FOR
    SELECT 
        f.FACTURA_ID,                  -- Invoice ID
        f.CLIENTE_ID,                  -- Client ID
        c.NOMBRE AS CLIENTE_NOMBRE,    -- Client Name from CLIENTES table
        f.MONTO,                       -- Invoice Amount
        f.FECHA_FACTURA,               -- Date of the invoice
        f.ESTADO_PAGO                  -- Payment Status
    FROM 
        FACTURACION f
    JOIN 
        CLIENTES c ON f.CLIENTE_ID = c.CLIENTE_ID  -- Join CLIENTES to get the name
    WHERE 
        f.CLIENTE_ID = p_cliente_id;  -- Filter by the provided client ID
END ConsultaFacturacion;

