--  DDL for Procedure SP_VER_ENTRENADOR
--------------------------------------------------------
set define off;

  CREATE OR REPLACE NONEDITIONABLE PROCEDURE "ESTUDIANTE"."SP_VER_ENTRENADOR" (
    p_entrenador_id IN INT
)
IS
BEGIN
    -- Mostrar los campos especficos del entrenador con el ID especificado
    FOR r IN (SELECT nombre_entrenador, 
                      especialidad, 
                      disponibilidad, 
                      perfil
               FROM Entrenadores
               WHERE entrenador_id = p_entrenador_id) LOOP
        DBMS_OUTPUT.PUT_LINE('Nombre: ' || r.nombre_entrenador);
        DBMS_OUTPUT.PUT_LINE('Especialidad: ' || r.especialidad);
        DBMS_OUTPUT.PUT_LINE('Disponibilidad: ' || r.disponibilidad);
        DBMS_OUTPUT.PUT_LINE('Perfil: ' || r.perfil);
    END LOOP;
EXCEPTION
    WHEN NO_DATA_FOUND THEN
        DBMS_OUTPUT.PUT_LINE('No se encontr entrenador con ID ' || p_entrenador_id);
    WHEN OTHERS THEN
        DBMS_OUTPUT.PUT_LINE('Error: ' || SQLERRM);
END sp_ver_entrenador;

